// src/components/Chat.js

import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import io from 'socket.io-client';
import { encryptMessage, decryptMessage, hideDataInImage, extractDataFromImage, hideDataInVideoFrame, extractDataFromVideo } from '../utils/crypto';

const Chat = ({ userId }) => {
    const [users, setUsers] = useState([]);
    const [recipient, setRecipient] = useState('');
    const [currentMessage, setCurrentMessage] = useState('');
    const [messages, setMessages] = useState([]);
    const [selectedFile, setSelectedFile] = useState(null);
    const [selectedCarrierType, setSelectedCarrierType] = useState('image'); // 'image' or 'video'
    const [isSending, setIsSending] = useState(false);
    const socket = useRef(null);

    useEffect(() => {
        socket.current = io('http://localhost:3001');
        socket.current.emit('register_user', userId);

        // UPDATED: This now receives an image URL or video URL and displays it
        socket.current.on('receive_message', (payload) => {
            const { mediaUrl, mediaType } = payload;
            // Add a new message of type 'image' or 'video' to be rendered
            setMessages((prev) => [...prev, {
                type: mediaType || 'image',
                sender: 'other',
                content: mediaUrl // The content is the URL to the stego-media
            }]);
        });

        const fetchUsers = async () => {
            const response = await axios.get('http://localhost:3001/api/users');
            setUsers(response.data.filter(u => u !== userId));
        };
        fetchUsers();

        return () => socket.current.disconnect();
    }, [userId]);

    const dataURLtoBlob = (dataurl) => {
        const arr = dataurl.split(','), mime = arr[0].match(/:(.*?);/)[1];
        const bstr = atob(arr[1]);
        let n = bstr.length;
        const u8arr = new Uint8Array(n);
        while(n--){ u8arr[n] = bstr.charCodeAt(n); }
        return new Blob([u8arr], {type:mime});
    }

    const sendMessage = async () => {
        if (!currentMessage || !recipient || !selectedFile) {
            alert(`Please type a message and select a carrier ${selectedCarrierType}.`);
            return;
        }
        setIsSending(true);
        try {
            const res = await axios.get(`http://localhost:3001/api/key/${recipient}`);
            const encryptedPayload = encryptMessage(currentMessage, res.data.publicKey);
            const encryptedDataString = JSON.stringify(encryptedPayload);

            const reader = new FileReader();
            reader.onload = async (event) => {
                try {
                    let uploadData = null;
                    let mediaType = selectedCarrierType;

                    if (selectedCarrierType === 'image') {
                        // Original image steganography
                        const originalImageDataUrl = event.target.result;
                        const stegoImageDataUrl = await hideDataInImage(originalImageDataUrl, encryptedDataString);
                        
                        const blob = dataURLtoBlob(stegoImageDataUrl);
                        const formData = new FormData();
                        formData.append('carrier', blob, 'stego-image.png');
                        const uploadRes = await axios.post('http://localhost:3001/api/upload', formData);
                        uploadData = uploadRes.data;
                    } else if (selectedCarrierType === 'video') {
                        // Video steganography
                        const result = await hideDataInVideoFrame(selectedFile, encryptedDataString);
                        
                        // Convert stegoFrame data URL to Blob
                        const stegoFrameBlob = dataURLtoBlob(result.stegoFrame);
                        
                        const formData = new FormData();
                        formData.append('carrier', selectedFile, selectedFile.name);
                        formData.append('stegoFrame', stegoFrameBlob, 'stego-frame.png');
                        formData.append('frameTimestamp', result.frameMetadata.frameTimestamp);
                        
                        const uploadRes = await axios.post('http://localhost:3001/api/upload', formData, {
                            timeout: 60000, // 60 seconds for large files
                            maxContentLength: Infinity,
                            maxBodyLength: Infinity
                        });
                        uploadData = uploadRes.data;
                        mediaType = 'video';
                    }

                    socket.current.emit('send_message', {
                        recipientId: recipient,
                        message: { 
                            mediaUrl: uploadData.filePath,
                            mediaType: mediaType,
                            frameTimestamp: uploadData.frameTimestamp || null
                        },
                    });

                    // The sender sees their own text message immediately
                    setMessages((prev) => [...prev, { type: 'text', sender: 'me', content: currentMessage }]);
                    setCurrentMessage('');
                    setSelectedFile(null);
                } catch (error) {
                    console.error("Failed to process carrier:", error);
                    alert('Failed to process ' + selectedCarrierType + '. ' + error.message);
                }
            };
            reader.readAsDataURL(selectedFile);
        } catch (error) {
            console.error("Failed to send message:", error);
            alert('Failed to send message: ' + error.message);
        } finally {
            setIsSending(false);
        }
    };

    // --- NEW: Function to handle revealing the hidden text from image ---
    const handleRetrieveText = async (mediaUrl, index) => {
        try {
            const myPrivateKey = localStorage.getItem(`privateKey_${userId}`);
            if (!myPrivateKey) return;

            const mediaResponse = await axios.get(`http://localhost:3001${mediaUrl}`, { responseType: 'blob' });
            
            const reader = new FileReader();
            reader.onload = async () => {
                const mediaDataUrl = reader.result;
                const encryptedDataString = await extractDataFromImage(mediaDataUrl);
                const encryptedPayload = JSON.parse(encryptedDataString);
                const decryptedMessage = decryptMessage(encryptedPayload, myPrivateKey);

                // Update the messages array: replace the media object with a text object
                const updatedMessages = [...messages];
                updatedMessages[index] = { type: 'text', sender: 'other', content: decryptedMessage };
                setMessages(updatedMessages);
            };
            reader.readAsDataURL(mediaResponse.data);
        } catch (error) {
            console.error('Failed to retrieve text:', error);
            alert('Could not retrieve text from media. It may be corrupted.');
        }
    };

    // --- NEW: Function to handle revealing the hidden text from video frame ---
    const handleRetrieveTextFromVideo = async (videoUrl, frameTimestamp, index) => {
        try {
            const myPrivateKey = localStorage.getItem(`privateKey_${userId}`);
            if (!myPrivateKey) return;

            const videoResponse = await axios.get(`http://localhost:3001${videoUrl}`, { responseType: 'blob' });
            
            // Extract data from the specific frame in the video
            const encryptedDataString = await extractDataFromVideo(videoResponse.data, frameTimestamp);
            const encryptedPayload = JSON.parse(encryptedDataString);
            const decryptedMessage = decryptMessage(encryptedPayload, myPrivateKey);

            // Update the messages array: replace the video object with a text object
            const updatedMessages = [...messages];
            updatedMessages[index] = { type: 'text', sender: 'other', content: decryptedMessage };
            setMessages(updatedMessages);
        } catch (error) {
            console.error('Failed to retrieve text from video:', error);
            alert('Could not retrieve text from video. It may be corrupted.');
        }
    };

    const handleLogout = () => window.location.reload();

    return (
        <div className="chat-container">
            <div className="sidebar">
                <h3>Users</h3>
                <ul>
                    {users.map((user) => (
                        <li key={user} onClick={() => setRecipient(user)} className={recipient === user ? 'active' : ''}>
                            {user}
                        </li>
                    ))}
                </ul>
            </div>
            <div className="main-chat">
                <div className="chat-header">
                    <h2>Chatting with: {recipient || 'Select a user'}</h2>
                    <p>Logged in as: <strong>{userId}</strong></p>
                    <button onClick={handleLogout}>Logout</button>
                </div>
                <div className="message-window">
                    {/* --- UPDATED: Rendering logic for different message types --- */}
                    {messages.map((msg, index) => (
                        <div key={index} className={`message ${msg.sender}`}>
                            {msg.type === 'text' ? (
                                // If it's a text message, just display the content
                                msg.content
                            ) : msg.type === 'image' ? (
                                // If it's an image, display the image and a button
                                <div className="stego-image-container">
                                    <img src={`http://localhost:3001${msg.content}`} alt="Hidden message" />
                                    <button onClick={() => handleRetrieveText(msg.content, index)}>
                                        Retrieve Text from Image
                                    </button>
                                </div>
                            ) : msg.type === 'video' ? (
                                // If it's a video, display the video and a button
                                <div className="stego-video-container">
                                    <video width="320" height="240" controls>
                                        <source src={`http://localhost:3001${msg.content}`} type="video/mp4" />
                                        Your browser does not support the video tag.
                                    </video>
                                    <button onClick={() => {
                                        // For now, extract from first frame
                                        // In future: show frame picker UI
                                        const frameTimestamp = msg.frameTimestamp || 1;
                                        handleRetrieveTextFromVideo(msg.content, frameTimestamp, index);
                                    }}>
                                        Retrieve Text from Video
                                    </button>
                                </div>
                            ) : null}
                        </div>
                    ))}
                </div>
                <div className="message-input">
                    <div className="carrier-type-selector">
                        <label>
                            <input 
                                type="radio" 
                                value="image" 
                                checked={selectedCarrierType === 'image'}
                                onChange={(e) => {
                                    setSelectedCarrierType(e.target.value);
                                    setSelectedFile(null);
                                }}
                            />
                            Image
                        </label>
                        <label>
                            <input 
                                type="radio" 
                                value="video" 
                                checked={selectedCarrierType === 'video'}
                                onChange={(e) => {
                                    setSelectedCarrierType(e.target.value);
                                    setSelectedFile(null);
                                }}
                            />
                            Video
                        </label>
                    </div>
                    <input 
                        type="file" 
                        accept={selectedCarrierType === 'image' ? "image/png, image/jpeg" : "video/mp4, video/webm, video/quicktime"}
                        onChange={(e) => setSelectedFile(e.target.files[0])} 
                    />
                    <input type="text" placeholder="Type a message..." value={currentMessage} onChange={(e) => setCurrentMessage(e.target.value)} disabled={!recipient} />
                    <button onClick={sendMessage} disabled={!recipient || isSending}>
                        {isSending ? 'Sending...' : 'Send'}
                    </button>
                </div>
            </div>
        </div>
    );
};

export default Chat;
