// src/components/Login.js

import React, { useState } from 'react';
import axios from 'axios';

// 1. Accept the new prop
const Login = ({ onLoginSuccess }) => { 
    const [userId, setUserId] = useState('');
    const [password, setPassword] = useState('');
    const [message, setMessage] = useState('');
    const [messageType, setMessageType] = useState(''); // 'success' or 'error'

    const handleLogin = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post('http://localhost:3001/api/login', {
                userId,
                password,
            });

            // Store the private key in localStorage for decryption
            if (response.data.privateKey) {
                localStorage.setItem(`privateKey_${response.data.userId}`, response.data.privateKey);
                console.log('Private key stored for user:', response.data.userId);
            }
            
            setMessage('Login successful! Redirecting...');
            setMessageType('success');
            
            // 2. On success, call the function from App.js
            setTimeout(() => {
                onLoginSuccess(response.data.token, response.data.userId);
            }, 500);

        } catch (error) {
            if (error.response && error.response.data) {
                setMessage(error.response.data.message);
            } else {
                setMessage('Login failed. Server might be down.');
            }
            setMessageType('error');
        }
    };

    return (
        <div className="auth-container login-container">
            <h2>ðŸ”‘ Login</h2>
            <form onSubmit={handleLogin}>
                <input
                    type="email"
                    placeholder="Enter Email / User ID"
                    value={userId}
                    onChange={(e) => setUserId(e.target.value)}
                    required
                />
                <input
                    type="password"
                    placeholder="Enter Your Password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    required
                />
                <button type="submit">Login Now</button>
            </form>
            {message && <p className={`message message-${messageType}`}>{message}</p>}
        </div>
    );
};

export default Login;
