// src/components/Chat.js

import React, { useState, useEffect, useRef } from "react";
import axios from "axios";
import io from "socket.io-client";
import {
    encryptMessage,
    decryptMessage,
    hideDataInImage,
    extractDataFromImage,
    hideDataInVideoFrame
} from "../utils/crypto";

const Chat = ({ userId }) => {
    const [users, setUsers] = useState([]);
    const [recipient, setRecipient] = useState("");
    const [currentMessage, setCurrentMessage] = useState("");
    const [messages, setMessages] = useState([]);
    const [selectedFile, setSelectedFile] = useState(null);
    const [selectedCarrierType, setSelectedCarrierType] = useState("image");
    const [isSending, setIsSending] = useState(false);
    const [decryptingIndex, setDecryptingIndex] = useState(null);

    const socket = useRef(null);

    // ---------------- SOCKET SETUP ----------------
    useEffect(() => {
        socket.current = io("http://localhost:3001");
        socket.current.emit("register_user", userId);

        socket.current.on("receive_message", (payload) => {
            const { mediaUrl, stegoFramePath, mediaType } = payload;

            setMessages((prev) => [
                ...prev,
                {
                    sender: "other",
                    content: "[Encrypted Message - Click to Decrypt]",
                    mediaUrl,
                    stegoFramePath,
                    mediaType,
                    isEncrypted: true,
                    isDecrypted: false,
                    threat: null
                }
            ]);
        });

        const fetchUsers = async () => {
            const res = await axios.get("http://localhost:3001/api/users");
            setUsers(res.data.filter((u) => u !== userId));
        };

        fetchUsers();

        return () => socket.current.disconnect();
    }, [userId]);

    // Convert base64 to blob
    const dataURLtoBlob = (dataurl) => {
        const arr = dataurl.split(",");
        const bstr = atob(arr[1]);
        let n = bstr.length;
        const u8arr = new Uint8Array(n);
        while (n--) u8arr[n] = bstr.charCodeAt(n);
        return new Blob([u8arr], { type: "image/png" });
    };

    // ---------------- SEND MESSAGE ----------------
    const sendMessage = async () => {
        if (!recipient || !currentMessage || !selectedFile) {
            alert("Select user, file, and message.");
            return;
        }

        setIsSending(true);

        try {
            const res = await axios.get(`http://localhost:3001/api/key/${recipient}`);
            const encryptedPayload = encryptMessage(currentMessage, res.data.publicKey);
            const encryptedString = JSON.stringify(encryptedPayload);

            const reader = new FileReader();

            reader.onload = async (event) => {
                let uploadData = null;

                if (selectedCarrierType === "image") {
                    const stego = await hideDataInImage(event.target.result, encryptedString);
                    const blob = dataURLtoBlob(stego);

                    const formData = new FormData();
                    formData.append("carrier", blob, "stego-img.png");

                    const uploadRes = await axios.post("http://localhost:3001/api/upload", formData);
                    uploadData = uploadRes.data;

                } else {
                    const result = await hideDataInVideoFrame(selectedFile, encryptedString);
                    const stegoBlob = dataURLtoBlob(result.stegoFrame);

                    const formData = new FormData();
                    formData.append("carrier", selectedFile, selectedFile.name);
                    formData.append("stegoFrame", stegoBlob, "stego-frame.png");
                    formData.append("frameTimestamp", result.frameMetadata.frameTimestamp);

                    const uploadRes = await axios.post("http://localhost:3001/api/upload", formData);
                    uploadData = uploadRes.data;
                }

                socket.current.emit("send_message", {
                    recipientId: recipient,
                    message: {
                        mediaUrl: uploadData.filePath,
                        stegoFramePath: uploadData.stegoFramePath,
                        mediaType: selectedCarrierType
                    }
                });

                setMessages((prev) => [
                    ...prev,
                    {
                        sender: "me",
                        content: currentMessage,
                        mediaUrl: uploadData.filePath,
                        stegoFramePath: uploadData.stegoFramePath,
                        mediaType: selectedCarrierType,
                        isDecrypted: true,
                        threat: null
                    }
                ]);

                setCurrentMessage("");
                setSelectedFile(null);
            };

            reader.readAsDataURL(selectedFile);

        } catch (err) {
            console.error(err);
            alert("Sending failed.");
        }

        setIsSending(false);
    };

    // ---------------- PHISHING DETECTION ----------------
    const detectThreat = async (text) => {
        try {
            const res = await axios.post("http://localhost:3001/api/phishing-detect", {
                message: text
            });
            return res.data.label;
        } catch (err) {
            console.error("Threat detection error:", err);
            return "unknown";
        }
    };

    // ---------------- DECRYPT IMAGE ----------------
    const handleRetrieveText = async (mediaUrl, index) => {
        setDecryptingIndex(index);

        try {
            const privateKey = localStorage.getItem(`privateKey_${userId}`);
            const img = await axios.get(`http://localhost:3001${mediaUrl}`, { responseType: "blob" });

            const reader = new FileReader();
            reader.onload = async () => {
                const hidden = await extractDataFromImage(reader.result);
                const decrypted = decryptMessage(JSON.parse(hidden), privateKey);

                const threat = await detectThreat(decrypted);

                const updated = [...messages];
                updated[index] = {
                    ...updated[index],
                    content: decrypted,
                    isDecrypted: true,
                    isEncrypted: false,
                    threat
                };

                setMessages(updated);
                setDecryptingIndex(null);
            };

            reader.readAsDataURL(img.data);

        } catch (err) {
            alert("Decrypt failed.");
            setDecryptingIndex(null);
        }
    };

    // ---------------- DECRYPT VIDEO ----------------
    const handleRetrieveTextFromVideo = async (stegoPath, index) => {
        setDecryptingIndex(index);

        try {
            const privateKey = localStorage.getItem(`privateKey_${userId}`);
            const frame = await axios.get(`http://localhost:3001${stegoPath}`, { responseType: "blob" });

            const reader = new FileReader();
            reader.onload = async () => {
                const hidden = await extractDataFromImage(reader.result);
                const decrypted = decryptMessage(JSON.parse(hidden), privateKey);

                const threat = await detectThreat(decrypted);

                const updated = [...messages];
                updated[index] = {
                    ...updated[index],
                    content: decrypted,
                    isDecrypted: true,
                    isEncrypted: false,
                    threat
                };

                setMessages(updated);
                setDecryptingIndex(null);
            };

            reader.readAsDataURL(frame.data);

        } catch (err) {
            alert("Decrypt video failed.");
            setDecryptingIndex(null);
        }
    };

    // ---------------- UI ----------------
    return (
        <div className="chat-wrapper">

            {/* Sidebar */}
            <aside className="sidebar">
                <div className="sidebar-header">Users</div>
                <ul className="user-list">
                    {users.map((u) => (
                        <li
                            key={u}
                            className={`user-item ${recipient === u ? "active" : ""}`}
                            onClick={() => setRecipient(u)}
                        >
                            <span className="user-circle"></span>
                            <span className="username">{u}</span>
                        </li>
                    ))}
                </ul>
            </aside>

            {/* Chat Section */}
            <main className="chat-section">

                <header className="top-header">
                    <h2 className="chat-title">
                        {recipient ? `Chatting with: ${recipient}` : "Choose a user"}
                    </h2>
                    <p className="login-user">Logged in as: <b>{userId}</b></p>
                </header>

                <div className="messages-area">
                    {messages.map((msg, i) => (
                        <div key={i} className={`msg-bubble ${msg.sender === "me" ? "me" : "other"}`}>

                            <div className="msg-text">{msg.content}</div>

                            {msg.isDecrypted && msg.threat && (
                                <div className={`threat-banner ${msg.threat === "fake" ? "danger" : "safe"}`}>
                                    {msg.threat === "fake"
                                        ? "‚ö† PHISHING DETECTED"
                                        : "‚úì SAFE MESSAGE"}
                                </div>
                            )}

                            {msg.mediaType === "image" && (
                                <div className="media-box">
                                    <img src={`http://localhost:3001${msg.mediaUrl}`} className="media-img" />
                                    {!msg.isDecrypted && (
                                        <button
                                            className="btn decrypt-btn"
                                            onClick={() => handleRetrieveText(msg.mediaUrl, i)}
                                            disabled={decryptingIndex === i}
                                        >
                                            {decryptingIndex === i ? "Decrypting..." : "üîç Reveal Text"}
                                        </button>
                                    )}
                                </div>
                            )}

                            {msg.mediaType === "video" && (
                                <div className="media-box">
                                    <video controls className="media-video">
                                        <source src={`http://localhost:3001${msg.mediaUrl}`} />
                                    </video>
                                    {!msg.isDecrypted && (
                                        <button
                                            className="btn decrypt-btn"
                                            onClick={() => handleRetrieveTextFromVideo(msg.stegoFramePath, i)}
                                        >
                                            üéû Reveal Text
                                        </button>
                                    )}
                                </div>
                            )}

                        </div>
                    ))}
                </div>

                {/* Input Bar */}
                <footer className="input-area">

                    <div className="carrier-select">
                        <label>
                            <input
                                type="radio"
                                value="image"
                                checked={selectedCarrierType === "image"}
                                onChange={() => setSelectedCarrierType("image")}
                            />
                            Image
                        </label>

                        <label>
                            <input
                                type="radio"
                                value="video"
                                checked={selectedCarrierType === "video"}
                                onChange={() => setSelectedCarrierType("video")}
                            />
                            Video
                        </label>
                    </div>

                    <input
                        type="file"
                        onChange={(e) => setSelectedFile(e.target.files[0])}
                        accept={selectedCarrierType === "image" ? "image/*" : "video/*"}
                    />

                    <input
                        type="text"
                        value={currentMessage}
                        onChange={(e) => setCurrentMessage(e.target.value)}
                        placeholder="Write message..."
                    />

                    <button onClick={sendMessage} disabled={isSending}>
                        ‚û§
                    </button>

                </footer>

            </main>

        </div>
    );
};

export default Chat;
