// src/components/Chat.js

import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import io from 'socket.io-client';
import { encryptMessage, decryptMessage, hideDataInImage, extractDataFromImage, hideDataInVideoFrame } from '../utils/crypto';

const Chat = ({ userId }) => {
    const [users, setUsers] = useState([]);
    const [recipient, setRecipient] = useState('');
    const [currentMessage, setCurrentMessage] = useState('');
    const [messages, setMessages] = useState([]);
    const [selectedFile, setSelectedFile] = useState(null);
    const [selectedCarrierType, setSelectedCarrierType] = useState('image');
    const [isSending, setIsSending] = useState(false);
    const [decryptingIndex, setDecryptingIndex] = useState(null); // Track which message is being decrypted
    const socket = useRef(null);

    // ‚≠ê ADDED FOR PHISHING DETECTION
    const classifyMessage = async (text) => {
        try {
            const res = await axios.post("http://localhost:3001/api/phishing-detect", {
                message: text
            });
            return res.data.label;  // fake or not_fake
        } catch (err) {
            console.error("Phishing Detection Error:", err);
            return "unknown";
        }
    };

    // SOCKET + USER LIST LOADING
    useEffect(() => {
        socket.current = io('http://localhost:3001');
        socket.current.emit('register_user', userId);

        socket.current.on('receive_message', (payload) => {
            const { mediaUrl, stegoFramePath, mediaType, frameTimestamp } = payload;

            if (!mediaUrl) return;

            setMessages((prev) => [...prev, {
                type: 'media-with-text',
                sender: 'other',
                content: '[Encrypted Message - Click to Decrypt]',
                mediaUrl,
                stegoFramePath,
                mediaType,
                frameTimestamp,
                isEncrypted: true,
                isDecrypted: false,
                phishingResult: null
            }]);
        });

        const fetchUsers = async () => {
            const response = await axios.get('http://localhost:3001/api/users');
            setUsers(response.data.filter(u => u !== userId));
        };
        fetchUsers();

        return () => socket.current.disconnect();
    }, [userId]);


    const dataURLtoBlob = (dataurl) => {
        const arr = dataurl.split(','), mime = arr[0].match(/:(.*?);/)[1];
        const bstr = atob(arr[1]); let n = bstr.length;
        const u8arr = new Uint8Array(n);
        while (n--) u8arr[n] = bstr.charCodeAt(n);
        return new Blob([u8arr], { type: mime });
    };


    // SEND MESSAGE (NO CHANGES HERE)
    const sendMessage = async () => {
        if (!currentMessage || !recipient || !selectedFile) {
            alert(`Please type a message and select a carrier ${selectedCarrierType}.`);
            return;
        }
        setIsSending(true);
        try {
            const res = await axios.get(`http://localhost:3001/api/key/${recipient}`);
            const encryptedPayload = encryptMessage(currentMessage, res.data.publicKey);
            const encryptedDataString = JSON.stringify(encryptedPayload);

            const reader = new FileReader();
            reader.onload = async (event) => {
                try {
                    let uploadData;
                    let mediaType = selectedCarrierType;

                    if (selectedCarrierType === 'image') {
                        const originalImageDataUrl = event.target.result;
                        const stegoImageDataUrl = await hideDataInImage(originalImageDataUrl, encryptedDataString);

                        const blob = dataURLtoBlob(stegoImageDataUrl);
                        const formData = new FormData();
                        formData.append('carrier', blob, 'stego-image.png');

                        const uploadRes = await axios.post('http://localhost:3001/api/upload', formData);
                        uploadData = uploadRes.data;

                    } else if (selectedCarrierType === 'video') {
                        const result = await hideDataInVideoFrame(selectedFile, encryptedDataString);

                        const stegoFrameBlob = dataURLtoBlob(result.stegoFrame);
                        const formData = new FormData();
                        formData.append('carrier', selectedFile, selectedFile.name);
                        formData.append('stegoFrame', stegoFrameBlob, 'stego-frame.png');
                        formData.append('frameTimestamp', result.frameMetadata.frameTimestamp);

                        const uploadRes = await axios.post('http://localhost:3001/api/upload', formData, {
                            timeout: 60000,
                            maxContentLength: Infinity,
                            maxBodyLength: Infinity
                        });
                        uploadData = uploadRes.data;
                        mediaType = 'video';
                    }

                    socket.current.emit('send_message', {
                        recipientId: recipient,
                        message: {
                            mediaUrl: uploadData.filePath,
                            stegoFramePath: uploadData.stegoFramePath,
                            mediaType,
                            frameTimestamp: uploadData.frameTimestamp || null,
                            textMessage: currentMessage
                        }
                    });

                    setMessages((prev) => [...prev, {
                        type: 'media-with-text',
                        sender: 'me',
                        content: currentMessage,
                        mediaUrl: uploadData.filePath,
                        stegoFramePath: uploadData.stegoFramePath,
                        mediaType,
                        frameTimestamp: uploadData.frameTimestamp || null
                    }]);

                    setCurrentMessage('');
                    setSelectedFile(null);

                } catch (error) {
                    console.error("Failed:", error);
                    alert("Failed to process carrier.");
                }
            };
            reader.readAsDataURL(selectedFile);

        } catch (error) {
            console.error("Failed:", error);
        } finally {
            setIsSending(false);
        }
    };


    // ‚≠ê UPDATED: DECRYPT IMAGE + PHISHING DETECTION WITH TIMEOUT
    const handleRetrieveText = async (mediaUrl, index) => {
        setDecryptingIndex(index); // Show loading state
        try {
            const myPrivateKey = localStorage.getItem(`privateKey_${userId}`);
            console.log('Retrieved private key:', myPrivateKey ? 'Present' : 'Missing');
            
            if (!myPrivateKey) {
                alert("Private key missing. Please logout and login again.");
                setDecryptingIndex(null);
                return;
            }

            console.log('Downloading image from:', mediaUrl);
            const mediaResponse = await axios.get(`http://localhost:3001${mediaUrl}`, { 
                responseType: 'blob',
                timeout: 30000 // 30 second timeout
            });
            console.log('Image downloaded, size:', mediaResponse.data.size);

            const reader = new FileReader();
            reader.onload = async () => {
                try {
                    const mediaDataUrl = reader.result;
                    console.log('Starting image data extraction...');
                    
                    const encryptedDataString = await extractDataFromImage(mediaDataUrl);
                    console.log('Extracted encrypted data length:', encryptedDataString.length);
                    console.log('Extracted data (first 100 chars):', encryptedDataString.substring(0, 100));
                    
                    let encryptedPayload;
                    try {
                        encryptedPayload = JSON.parse(encryptedDataString);
                        console.log('Parsed encrypted payload:', Object.keys(encryptedPayload));
                    } catch (parseError) {
                        console.error('JSON parse error:', parseError.message);
                        console.error('Raw data that failed to parse:', encryptedDataString.substring(0, 200));
                        alert("Encrypted data is corrupted. Cannot parse JSON.\n\nTry sending the message again.");
                        setDecryptingIndex(null);
                        return;
                    }

                    try {
                        console.log('Starting decryption...');
                        const decryptedMessage = decryptMessage(encryptedPayload, myPrivateKey);
                        console.log('Decryption successful, message length:', decryptedMessage.length);
                        console.log('Decrypted message:', decryptedMessage);
                        console.log('Current messages array length before update:', messages.length);

                        // ‚≠ê PHISHING DETECTION
                        const phishingLabel = await classifyMessage(decryptedMessage);
                        console.log('Phishing label:', phishingLabel);

                        const updatedMessages = [...messages];
                        console.log('Message at index ' + index + ' before:', updatedMessages[index]);
                        
                        updatedMessages[index] = {
                            ...updatedMessages[index],
                            content: decryptedMessage,
                            isDecrypted: true,
                            phishingResult: phishingLabel
                        };
                        
                        console.log('Message at index ' + index + ' after:', updatedMessages[index]);
                        console.log('Setting messages state with updated array length:', updatedMessages.length);

                        setMessages(updatedMessages);
                        setDecryptingIndex(null);
                        console.log('State update complete - UI should re-render now');
                    } catch (decryptError) {
                        console.error('Decryption error:', decryptError.message);
                        console.error('Decryption stack:', decryptError.stack);
                        alert("Decryption failed: " + decryptError.message + "\n\nMake sure you are the intended recipient.");
                        setDecryptingIndex(null);
                    }
                } catch (innerError) {
                    console.error('Error during extraction process:', innerError);
                    console.error('Extraction stack:', innerError.stack);
                    alert("Extraction failed: " + innerError.message + "\n\nTry again or send the message again.");
                    setDecryptingIndex(null);
                }
            };
            reader.onerror = () => {
                alert("Failed to read image file");
                setDecryptingIndex(null);
            };
            reader.readAsDataURL(mediaResponse.data);

        } catch (e) {
            console.error('Outer error:', e);
            alert("Could not decrypt message: " + (e.message || e) + "\n\nPlease try again.");
            setDecryptingIndex(null);
        }
    };


    // ‚≠ê UPDATED: DECRYPT VIDEO + PHISHING DETECTION WITH TIMEOUT
    const handleRetrieveTextFromVideo = async (stegoFramePath, mediaUrl, frameTimestamp, index) => {
        setDecryptingIndex(index); // Show loading state
        try {
            const myPrivateKey = localStorage.getItem(`privateKey_${userId}`);
            console.log('Retrieved private key:', myPrivateKey ? 'Present' : 'Missing');
            
            if (!myPrivateKey) {
                alert("Private key missing. Please logout and login again.");
                setDecryptingIndex(null);
                return;
            }

            if (!stegoFramePath) {
                alert("Stego frame path missing. Cannot retrieve message.");
                setDecryptingIndex(null);
                return;
            }

            console.log('Fetching stego frame from:', stegoFramePath);
            const frameResponse = await axios.get(`http://localhost:3001${stegoFramePath}`, { 
                responseType: 'blob',
                timeout: 30000 // 30 second timeout
            });
            console.log('Frame downloaded, size:', frameResponse.data.size);
            
            const reader = new FileReader();
            reader.onload = async () => {
                try {
                    const frameDataUrl = reader.result;
                    console.log('Starting frame data extraction...');
                    
                    const encryptedDataString = await extractDataFromImage(frameDataUrl);
                    console.log('Extracted encrypted data length:', encryptedDataString.length);
                    console.log('Extracted data (first 100 chars):', encryptedDataString.substring(0, 100));
                    
                    let encryptedPayload;
                    try {
                        encryptedPayload = JSON.parse(encryptedDataString);
                        console.log('Parsed encrypted payload:', Object.keys(encryptedPayload));
                    } catch (parseError) {
                        console.error('JSON parse error:', parseError.message);
                        console.error('Raw data that failed to parse:', encryptedDataString.substring(0, 200));
                        alert("Encrypted data is corrupted. Cannot parse JSON.\n\nTry sending the message again.");
                        setDecryptingIndex(null);
                        return;
                    }

                    try {
                        console.log('Starting decryption...');
                        const decryptedMessage = decryptMessage(encryptedPayload, myPrivateKey);
                        console.log('Decryption successful, message length:', decryptedMessage.length);
                        console.log('Decrypted message:', decryptedMessage);
                        console.log('Current messages array length before update:', messages.length);

                        // ‚≠ê PHISHING DETECTION
                        const phishingLabel = await classifyMessage(decryptedMessage);
                        console.log('Phishing label:', phishingLabel);

                        const updatedMessages = [...messages];
                        console.log('Message at index ' + index + ' before:', updatedMessages[index]);
                        
                        updatedMessages[index] = {
                            ...updatedMessages[index],
                            content: decryptedMessage,
                            isDecrypted: true,
                            phishingResult: phishingLabel
                        };
                        
                        console.log('Message at index ' + index + ' after:', updatedMessages[index]);
                        console.log('Setting messages state with updated array length:', updatedMessages.length);

                        setMessages(updatedMessages);
                        setDecryptingIndex(null);
                        console.log('State update complete - UI should re-render now');
                    } catch (decryptError) {
                        console.error('Decryption error:', decryptError.message);
                        console.error('Decryption stack:', decryptError.stack);
                        alert("Video decryption failed: " + decryptError.message + "\n\nMake sure you are the intended recipient.");
                        setDecryptingIndex(null);
                    }
                } catch (innerError) {
                    console.error('Error during extraction process:', innerError);
                    console.error('Extraction stack:', innerError.stack);
                    alert("Frame extraction failed: " + innerError.message + "\n\nTry again or send the message again.");
                    setDecryptingIndex(null);
                }
            };
            reader.onerror = () => {
                alert("Failed to read frame file");
                setDecryptingIndex(null);
            };
            reader.readAsDataURL(frameResponse.data);

        } catch (e) {
            console.error('Outer error:', e);
            alert("Failed to decrypt video message: " + (e.message || e) + "\n\nPlease try again.");
            setDecryptingIndex(null);
        }
    };

    const handleLogout = () => window.location.reload();


    return (
        <div className="chat-container">
            <div className="sidebar">
                <h3>Users</h3>
                <ul>
                    {users.map((user) => (
                        <li key={user} onClick={() => setRecipient(user)} className={recipient === user ? 'active' : ''}>
                            {user}
                        </li>
                    ))}
                </ul>
            </div>

            <div className="main-chat">
                <div className="chat-header">
                    <h2>Chatting with: {recipient || 'Select a user'}</h2>
                    <p>Logged in as: <strong>{userId}</strong></p>
                    <button onClick={handleLogout}>Logout</button>
                </div>

                <div className="message-window">
                    {messages.map((msg, index) => (
                        <div key={index} className={`message ${msg.sender}`}>

                            <div className="message-text">
                                <strong>Message:</strong> {msg.content}
                            </div>

                            {/* ‚≠ê Display phishing detection result */}
                            {msg.isDecrypted && msg.phishingResult && (
                                <div style={{
                                    marginTop: "6px",
                                    padding: "8px",
                                    borderRadius: "6px",
                                    background: msg.phishingResult === "fake" ? "#ffcccc" : "#ccffcc",
                                    color: msg.phishingResult === "fake" ? "#b30000" : "#006600"
                                }}>
                                    {msg.phishingResult === "fake"
                                        ? "üö® PHISHING MESSAGE DETECTED"
                                        : "‚úî SAFE MESSAGE"}
                                </div>
                            )}

                            {msg.mediaType === 'image' && (
                                <div className="stego-image-container">
                                    <img src={`http://localhost:3001${msg.mediaUrl}`} alt="Hidden" />
                                    {!msg.isDecrypted && (
                                        <button 
                                            onClick={() => handleRetrieveText(msg.mediaUrl, index)}
                                            disabled={decryptingIndex === index}
                                        >
                                            {decryptingIndex === index ? '‚è≥ Decrypting...' : 'Retrieve Text from Image'}
                                        </button>
                                    )}
                                </div>
                            )}

                            {msg.mediaType === 'video' && (
                                <div className="stego-video-container">
                                    <video width="320" height="240" controls>
                                        <source src={`http://localhost:3001${msg.mediaUrl}`} type="video/mp4" />
                                    </video>
                                    {!msg.isDecrypted && (
                                        <button 
                                            onClick={() =>
                                                handleRetrieveTextFromVideo(msg.stegoFramePath, msg.mediaUrl, msg.frameTimestamp, index)
                                            }
                                            disabled={decryptingIndex === index}
                                        >
                                            {decryptingIndex === index ? '‚è≥ Decrypting...' : 'Retrieve Text from Video'}
                                        </button>
                                    )}
                                </div>
                            )}

                        </div>
                    ))}
                </div>

                <div className="message-input">
                    <div className="carrier-type-selector">
                        <label>
                            <input type="radio"
                                value="image"
                                checked={selectedCarrierType === 'image'}
                                onChange={(e) => {
                                    setSelectedCarrierType(e.target.value);
                                    setSelectedFile(null);
                                }}
                            />
                            Image
                        </label>

                        <label>
                            <input type="radio"
                                value="video"
                                checked={selectedCarrierType === 'video'}
                                onChange={(e) => {
                                    setSelectedCarrierType(e.target.value);
                                    setSelectedFile(null);
                                }}
                            />
                            Video
                        </label>
                    </div>

                    <input
                        type="file"
                        accept={selectedCarrierType === 'image'
                            ? "image/png, image/jpeg"
                            : "video/mp4, video/webm, video/quicktime"
                        }
                        onChange={(e) => setSelectedFile(e.target.files[0])}
                    />

                    <input type="text"
                        placeholder="Type a message..."
                        value={currentMessage}
                        onChange={(e) => setCurrentMessage(e.target.value)}
                        disabled={!recipient}
                    />

                    <button onClick={sendMessage} disabled={!recipient || isSending}>
                        {isSending ? 'Sending...' : 'Send'}
                    </button>
                </div>
            </div>
        </div>
    );
};

export default Chat;
