// src/components/Register.js

import React, { useState } from 'react';
import axios from 'axios';

const Register = () => {
    const [userId, setUserId] = useState('');
    const [password, setPassword] = useState('');
    const [message, setMessage] = useState('');
    const [messageType, setMessageType] = useState(''); // 'success' or 'error'

    const handleRegister = async (e) => {
        e.preventDefault(); // Prevent form from reloading the page
        
        try {
            const response = await axios.post('http://localhost:3001/api/register', { userId, password });
            
            // NEW: This line saves the private key to the browser's local storage
            localStorage.setItem(`privateKey_${userId}`, response.data.privateKey);
            
            setMessage(response.data.message); // Display success message
            setMessageType('success');
            setUserId('');
            setPassword('');
        } catch (error) {
            // Display error message from the backend
            if (error.response && error.response.data) {
                setMessage(error.response.data.message);
            } else {
                setMessage('Registration failed. Server might be down.');
            }
            setMessageType('error');
        }
    };

    return (
        <div className="auth-container register-container">
            <h2>üîê Register</h2>
            <form onSubmit={handleRegister}>
                <input
                    type="email"
                    placeholder="Enter Email / User ID"
                    value={userId}
                    onChange={(e) => setUserId(e.target.value)}
                    required
                />
                <input
                    type="password"
                    placeholder="Enter a Strong Password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    required
                />
                <button type="submit">Register Now</button>
            </form>
            {message && <p className={`message message-${messageType}`}>{message}</p>}
        </div>
    );
};

export default Register;
