// src/components/Chat.js

import React, { useState, useEffect, useRef } from "react";
import axios from "axios";
import io from "socket.io-client";
import {
    encryptMessage,
    decryptMessage,
    hideDataInImage,
    extractDataFromImage,
    hideDataInVideoFrame,
    extractDataFromVideo
} from "../utils/crypto";

const API_BASE = "http://localhost:3001";

const Chat = ({ userId }) => {
    const [users, setUsers] = useState([]);
    const [recipient, setRecipient] = useState("");
    const [currentMessage, setCurrentMessage] = useState("");
    const [messages, setMessages] = useState([]);
    const [selectedFile, setSelectedFile] = useState(null);
    const [selectedCarrierType, setSelectedCarrierType] = useState("image");
    const [isSending, setIsSending] = useState(false);
    const [decryptingIndex, setDecryptingIndex] = useState(null);

    const socket = useRef(null);

    useEffect(() => {
        socket.current = io(API_BASE);
        socket.current.emit("register_user", userId);

        socket.current.on("receive_message", (payload) => {
            const { mediaUrl, stegoFramePath, mediaType, frameTimestamp } = payload;

            setMessages((prev) => [
                ...prev,
                {
                    sender: "other",
                    content: "[Encrypted Message - Click to Decrypt]",
                    mediaUrl,
                    stegoFramePath,
                    mediaType,
                    frameTimestamp,
                    isEncrypted: true,
                    isDecrypted: false,
                    threat: null
                }
            ]);
        });

        const fetchUsers = async () => {
            try {
                const res = await axios.get(`${API_BASE}/api/users`);
                setUsers(res.data.filter((u) => u !== userId));
            } catch (err) {
                console.error("Failed to fetch users:", err);
            }
        };
        fetchUsers();

        return () => socket.current.disconnect();
    }, [userId]);

    // helper
    const dataURLtoBlob = (dataurl) => {
        const arr = dataurl.split(",");
        const mime = arr[0].match(/:(.*?);/)[1];
        const bstr = atob(arr[1]);
        let n = bstr.length;
        const u8arr = new Uint8Array(n);
        while (n--) u8arr[n] = bstr.charCodeAt(n);
        return new Blob([u8arr], { type: mime });
    };

    // SEND
    const sendMessage = async () => {
        if (!recipient || !currentMessage || !selectedFile) {
            alert("Please pick a recipient, type a message and select a carrier file.");
            return;
        }
        setIsSending(true);
        try {
            const res = await axios.get(`${API_BASE}/api/key/${recipient}`);
            const encryptedPayload = encryptMessage(currentMessage, res.data.publicKey);
            const encryptedString = JSON.stringify(encryptedPayload);

            const reader = new FileReader();
            reader.onload = async (event) => {
                let uploadData = null;
                let mediaType = selectedCarrierType;

                if (selectedCarrierType === "image") {
                    const stegoImage = await hideDataInImage(event.target.result, encryptedString);
                    const blob = dataURLtoBlob(stegoImage);

                    const formData = new FormData();
                    formData.append("carrier", blob, "stego-image.png");
                    const uploadRes = await axios.post(`${API_BASE}/api/upload`, formData);
                    uploadData = uploadRes.data;
                } else {
                    const result = await hideDataInVideoFrame(selectedFile, encryptedString);
                    const stegoBlob = dataURLtoBlob(result.stegoFrame);

                    const formData = new FormData();
                    formData.append("carrier", selectedFile, selectedFile.name);
                    formData.append("stegoFrame", stegoBlob, "stego-frame.png");
                    formData.append("frameTimestamp", result.frameMetadata.frameTimestamp);

                    const uploadRes = await axios.post(`${API_BASE}/api/upload`, formData, {
                        timeout: 60000,
                        maxContentLength: Infinity,
                        maxBodyLength: Infinity
                    });
                    uploadData = uploadRes.data;
                    mediaType = "video";
                }

                // Emit to recipient
                socket.current.emit("send_message", {
                    recipientId: recipient,
                    message: {
                        mediaUrl: uploadData.filePath,
                        stegoFramePath: uploadData.stegoFramePath,
                        mediaType,
                        frameTimestamp: uploadData.frameTimestamp || null
                    }
                });

                // show message for sender immediately
                setMessages((prev) => [
                    ...prev,
                    {
                        sender: "me",
                        content: currentMessage,
                        mediaUrl: uploadData.filePath,
                        stegoFramePath: uploadData.stegoFramePath,
                        mediaType,
                        frameTimestamp: uploadData.frameTimestamp || null,
                        isDecrypted: true,
                        threat: null
                    }
                ]);

                setCurrentMessage("");
                setSelectedFile(null);
            };
            reader.readAsDataURL(selectedFile);

        } catch (err) {
            console.error("Send message error:", err);
            alert("Failed to send message.");
        } finally {
            setIsSending(false);
        }
    };

    // make phishing detect call with timeout and safe return
    const detectThreat = async (text, timeoutMs = 7000) => {
        if (text == null) return "unknown";
        try {
            const source = axios.CancelToken.source();
            const timeout = setTimeout(() => {
                source.cancel("phishing-detect-timeout");
            }, timeoutMs);

            const res = await axios.post(`${API_BASE}/api/phishing-detect`, { message: text }, { cancelToken: source.token });
            clearTimeout(timeout);
            return res.data.label || "unknown";
        } catch (err) {
            if (axios.isCancel(err)) {
                console.warn("Phishing detect timed out");
            } else {
                console.error("Phishing detect error", err.message || err);
            }
            return "unknown";
        }
    };

    // DECRYPT IMAGE: immediate UI update, then run threat detection
    const handleRetrieveText = async (mediaUrl, index) => {
        setDecryptingIndex(index);
        try {
            const myKey = localStorage.getItem(`privateKey_${userId}`);
            if (!myKey) {
                alert("Private key missing. Log out and login again.");
                setDecryptingIndex(null);
                return;
            }

            const img = await axios.get(`${API_BASE}${mediaUrl}`, { responseType: "blob" });
            const reader = new FileReader();
            reader.onload = async () => {
                try {
                    const hidden = await extractDataFromImage(reader.result);
                    const decrypted = decryptMessage(JSON.parse(hidden), myKey);

                    // Immediately update UI with decrypted text
                    setMessages((prev) => {
                        const copy = [...prev];
                        if (!copy[index]) return prev;
                        copy[index] = {
                            ...copy[index],
                            content: decrypted,
                            isDecrypted: true,
                            isEncrypted: false
                        };
                        return copy;
                    });
                    setDecryptingIndex(null);

                    // Then run threat detection in background and update message when done
                    const label = await detectThreat(decrypted, 7000);
                    if (label && label !== "unknown") {
                        setMessages((prev) => {
                            const copy = [...prev];
                            if (!copy[index]) return prev;
                            copy[index] = { ...copy[index], threat: label };
                            return copy;
                        });
                    }
                } catch (ex) {
                    console.error("Extraction/Decryption error:", ex);
                    alert("Failed to extract/decrypt message: " + (ex.message || ex));
                    setDecryptingIndex(null);
                }
            };
            reader.readAsDataURL(img.data);

        } catch (err) {
            console.error("Failed to download image for decryption:", err);
            alert("Failed to download image.");
            setDecryptingIndex(null);
        }
    };

    // DECRYPT VIDEO: same pattern (uses saved stego-frame)
    const handleRetrieveTextFromVideo = async (stegoFramePath, index) => {
        setDecryptingIndex(index);
        try {
            const myKey = localStorage.getItem(`privateKey_${userId}`);
            if (!myKey) {
                alert("Private key missing. Log out and login again.");
                setDecryptingIndex(null);
                return;
            }

            if (!stegoFramePath) {
                alert("No stego frame path available for this video.");
                setDecryptingIndex(null);
                return;
            }

            const frame = await axios.get(`${API_BASE}${stegoFramePath}`, { responseType: "blob" });
            const reader = new FileReader();
            reader.onload = async () => {
                try {
                    const hidden = await extractDataFromImage(reader.result);
                    const decrypted = decryptMessage(JSON.parse(hidden), myKey);

                    // immediate update
                    setMessages((prev) => {
                        const copy = [...prev];
                        if (!copy[index]) return prev;
                        copy[index] = {
                            ...copy[index],
                            content: decrypted,
                            isDecrypted: true,
                            isEncrypted: false
                        };
                        return copy;
                    });
                    setDecryptingIndex(null);

                    // background threat detection
                    const label = await detectThreat(decrypted, 7000);
                    if (label && label !== "unknown") {
                        setMessages((prev) => {
                            const copy = [...prev];
                            if (!copy[index]) return prev;
                            copy[index] = { ...copy[index], threat: label };
                            return copy;
                        });
                    }
                } catch (ex) {
                    console.error("Frame extraction/decryption error:", ex);
                    alert("Failed to extract/decrypt video frame: " + (ex.message || ex));
                    setDecryptingIndex(null);
                }
            };
            reader.readAsDataURL(frame.data);

        } catch (err) {
            console.error("Failed to download stego frame:", err);
            alert("Failed to download stego frame.");
            setDecryptingIndex(null);
        }
    };

    // UI (kept simple; your styled version can be used)
    return (
        <div className="chat-wrapper">
            <aside className="sidebar">
                <div className="sidebar-header">Users</div>
                <ul className="user-list">
                    {users.map((u) => (
                        <li
                            key={u}
                            className={`user-item ${recipient === u ? "active" : ""}`}
                            onClick={() => setRecipient(u)}
                        >
                            <span className="username">{u}</span>
                        </li>
                    ))}
                </ul>
            </aside>

            <main className="chat-section">
                <header className="top-header">
                    <div>
                        <h2 className="chat-title">
                            {recipient ? `Chatting with: ${recipient}` : "Select a user to start chat"}
                        </h2>
                        <p className="login-user">Logged in as: <b>{userId}</b></p>
                    </div>
                </header>

                <div className="messages-area">
                    {messages.map((msg, i) => (
                        <div key={i} className={`msg-bubble ${msg.sender === "me" ? "me" : "other"}`}>
                            <div className="msg-text">{msg.content}</div>

                            {/* threat banner if present */}
                            {msg.isDecrypted && msg.threat && (
                                <div className="threat-banner" style={{
                                    background: msg.threat === "fake" ? "#ffe6e6" : "#e6ffe6",
                                    color: msg.threat === "fake" ? "#b30000" : "#006600",
                                    padding: "6px 10px",
                                    borderRadius: 6,
                                    marginTop: 6,
                                    fontWeight: 600
                                }}>
                                    {msg.threat === "fake" ? "‚ö† PHISHING / MALICIOUS" : "‚úî SAFE"}
                                </div>
                            )}

                            {/* image */}
                            {msg.mediaType === "image" && (
                                <div className="media-box">
                                    <img src={`${API_BASE}${msg.mediaUrl}`} className="media-img" alt="stego" />
                                    {!msg.isDecrypted && (
                                        <button disabled={decryptingIndex === i} onClick={() => handleRetrieveText(msg.mediaUrl, i)}>
                                            {decryptingIndex === i ? "Decrypting..." : "üîç Reveal Text"}
                                        </button>
                                    )}
                                </div>
                            )}

                            {/* video */}
                            {msg.mediaType === "video" && (
                                <div className="media-box">
                                    <video controls className="media-video">
                                        <source src={`${API_BASE}${msg.mediaUrl}`} />
                                    </video>
                                    {!msg.isDecrypted && (
                                        <button disabled={decryptingIndex === i} onClick={() => handleRetrieveTextFromVideo(msg.stegoFramePath, i)}>
                                            {decryptingIndex === i ? "Decrypting..." : "üéû Reveal Text"}
                                        </button>
                                    )}
                                </div>
                            )}
                        </div>
                    ))}
                </div>

                <footer className="input-area">
                    <div className="carrier-select">
                        <label>
                            <input type="radio" value="image" checked={selectedCarrierType === "image"} onChange={() => { setSelectedCarrierType("image"); setSelectedFile(null); }} /> Image
                        </label>
                        <label>
                            <input type="radio" value="video" checked={selectedCarrierType === "video"} onChange={() => { setSelectedCarrierType("video"); setSelectedFile(null); }} /> Video
                        </label>
                    </div>

                    <input type="file" accept={selectedCarrierType === "image" ? "image/*" : "video/*"} onChange={(e) => setSelectedFile(e.target.files[0])} />
                    <input type="text" placeholder="Write a message..." value={currentMessage} onChange={(e) => setCurrentMessage(e.target.value)} disabled={!recipient} />
                    <button onClick={sendMessage} disabled={isSending}>{isSending ? "Sending..." : "Send"}</button>
                </footer>
            </main>
        </div>
    );
};

export default Chat;
