// src/components/Chat.js

import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import io from 'socket.io-client';
import {
    encryptMessage,
    decryptMessage,
    hideDataInImage,
    extractDataFromImage,
    hideDataInVideoFrame
} from '../utils/crypto';

const Chat = ({ userId }) => {
    const [users, setUsers] = useState([]);
    const [recipient, setRecipient] = useState('');
    const [currentMessage, setCurrentMessage] = useState('');
    const [messages, setMessages] = useState([]);
    const [selectedFile, setSelectedFile] = useState(null);
    const [selectedCarrierType, setSelectedCarrierType] = useState('image');
    const [isSending, setIsSending] = useState(false);
    const [decryptingIndex, setDecryptingIndex] = useState(null);
    const [allowedSends, setAllowedSends] = useState({}); // mapping from backend
    const socket = useRef(null);

    // Fetch users list + allowed-sends mapping on mount
    useEffect(() => {
        const init = async () => {
            try {
                const [usersRes, allowedRes] = await Promise.all([
                    axios.get('http://localhost:3001/api/users'),
                    axios.get('http://localhost:3001/api/allowed-sends')
                ]);
                setUsers(usersRes.data.filter(u => u !== userId));
                setAllowedSends(allowedRes.data || {});
            } catch (err) {
                console.error('Init fetch error', err);
            }
        };
        init();
    }, [userId]);

    // Setup socket
    useEffect(() => {
        socket.current = io('http://localhost:3001');
        socket.current.emit('register_user', userId);

        socket.current.on('receive_message', (payload) => {
            const { mediaUrl, stegoFramePath, mediaType, frameTimestamp, textMessage } = payload;

            // Push one new message placeholder for incoming stego-media
            setMessages(prev => [...prev, {
                type: 'media-with-text',
                sender: 'other',
                content: '[Encrypted Message - Click to Decrypt]',
                mediaUrl,
                stegoFramePath,
                mediaType,
                frameTimestamp,
                isEncrypted: true,
                isDecrypted: false,
                phishingResult: null
            }]);
        });

        // Optional: handle server notification when send blocked
        socket.current.on('send_blocked', (payload) => {
            alert(payload.reason || 'Send blocked by server.');
        });

        return () => socket.current.disconnect();
    }, [userId]);

    const canSendToSelected = () => {
        if (!recipient) return false;
        const allowed = allowedSends[recipient];
        if (!allowed || !Array.isArray(allowed)) return false;
        return allowed.includes(userId);
    };

    const dataURLtoBlob = (dataurl) => {
        const arr = dataurl.split(',');
        const mime = arr[0].match(/:(.*?);/)[1];
        const bstr = atob(arr[1]);
        let n = bstr.length;
        const u8arr = new Uint8Array(n);
        while (n--) u8arr[n] = bstr.charCodeAt(n);
        return new Blob([u8arr], { type: mime });
    };

    // SEND
    const sendMessage = async () => {
        if (!currentMessage || !recipient || !selectedFile) {
            alert(`Please type a message and select a carrier ${selectedCarrierType}.`);
            return;
        }
        if (!canSendToSelected()) {
            alert('You are not allowed to send to this recipient.');
            return;
        }

        setIsSending(true);
        try {
            const res = await axios.get(`http://localhost:3001/api/key/${recipient}`);
            const encryptedPayload = encryptMessage(currentMessage, res.data.publicKey);
            const encryptedDataString = JSON.stringify(encryptedPayload);

            const reader = new FileReader();
            reader.onload = async (event) => {
                try {
                    let uploadData;
                    let mediaType = selectedCarrierType;

                    if (selectedCarrierType === 'image') {
                        const originalImageDataUrl = event.target.result;
                        const stegoImageDataUrl = await hideDataInImage(originalImageDataUrl, encryptedDataString);

                        const blob = dataURLtoBlob(stegoImageDataUrl);
                        const formData = new FormData();
                        formData.append('carrier', blob, 'stego-image.png');
                        const uploadRes = await axios.post('http://localhost:3001/api/upload', formData);
                        uploadData = uploadRes.data;
                    } else {
                        const result = await hideDataInVideoFrame(selectedFile, encryptedDataString);
                        const stegoFrameBlob = dataURLtoBlob(result.stegoFrame);
                        const formData = new FormData();
                        formData.append('carrier', selectedFile, selectedFile.name);
                        formData.append('stegoFrame', stegoFrameBlob, 'stego-frame.png');
                        formData.append('frameTimestamp', result.frameMetadata.frameTimestamp);
                        const uploadRes = await axios.post('http://localhost:3001/api/upload', formData, {
                            timeout: 60000,
                            maxContentLength: Infinity,
                            maxBodyLength: Infinity
                        });
                        uploadData = uploadRes.data;
                        mediaType = 'video';
                    }

                    // Emit with senderId + recipientId for server-side enforcement
                    socket.current.emit('send_message', {
                        senderId: userId,
                        recipientId: recipient,
                        message: {
                            mediaUrl: uploadData.filePath,
                            stegoFramePath: uploadData.stegoFramePath,
                            mediaType: mediaType,
                            frameTimestamp: uploadData.frameTimestamp || null,
                            textMessage: currentMessage
                        }
                    });

                    // Add local copy to UI (sender view)
                    setMessages(prev => [...prev, {
                        type: 'media-with-text',
                        sender: 'me',
                        content: currentMessage,
                        mediaUrl: uploadData.filePath,
                        stegoFramePath: uploadData.stegoFramePath,
                        mediaType: mediaType,
                        frameTimestamp: uploadData.frameTimestamp || null,
                        isDecrypted: true,
                        phishingResult: null
                    }]);

                    setCurrentMessage('');
                    setSelectedFile(null);

                } catch (error) {
                    console.error("Failed to process carrier:", error);
                    alert('Failed to process ' + selectedCarrierType + '. ' + (error.message || ''));
                }
            };

            reader.readAsDataURL(selectedFile);
        } catch (error) {
            console.error("Failed to send message:", error);
            alert('Failed to send message: ' + (error.message || ''));
        } finally {
            setIsSending(false);
        }
    };

    // PHISHING classification
    const classifyMessage = async (text) => {
        try {
            const res = await axios.post('http://localhost:3001/api/phishing-detect', { message: text });
            return res.data.label;
        } catch (err) {
            console.error('Phishing Detection Error:', err);
            return 'unknown';
        }
    };

    // DECRYPT image
    const handleRetrieveText = async (mediaUrl, index) => {
        setDecryptingIndex(index);
        try {
            const myPrivateKey = localStorage.getItem(`privateKey_${userId}`);
            if (!myPrivateKey) {
                alert('Private key not found. Login again to retrieve your private key.');
                setDecryptingIndex(null);
                return;
            }

            const mediaResponse = await axios.get(`http://localhost:3001${mediaUrl}`, { responseType: 'blob' });

            const reader = new FileReader();
            reader.onload = async () => {
                try {
                    const mediaDataUrl = reader.result;
                    const encryptedDataString = await extractDataFromImage(mediaDataUrl);

                    if (!encryptedDataString) {
                        alert('No hidden data found in image.');
                        setDecryptingIndex(null);
                        return;
                    }

                    let encryptedPayload;
                    try {
                        encryptedPayload = JSON.parse(encryptedDataString);
                    } catch (e) {
                        console.error('JSON parse error:', e);
                        alert('Corrupted hidden data (not valid JSON).');
                        setDecryptingIndex(null);
                        return;
                    }

                    const decryptedMessage = decryptMessage(encryptedPayload, myPrivateKey);

                    const phishingLabel = await classifyMessage(decryptedMessage);

                    // Use functional update to avoid stale state
                    setMessages(prev => {
                        const updated = [...prev];
                        updated[index] = {
                            ...updated[index],
                            content: decryptedMessage,
                            isDecrypted: true,
                            phishingResult: phishingLabel
                        };
                        return updated;
                    });

                } catch (err) {
                    console.error('Failed to retrieve text:', err);
                    alert('Could not decrypt message: ' + (err.message || err));
                } finally {
                    setDecryptingIndex(null);
                }
            };
            reader.onerror = (err) => {
                console.error('FileReader error:', err);
                setDecryptingIndex(null);
            };
            reader.readAsDataURL(mediaResponse.data);
        } catch (err) {
            console.error('Outer error:', err);
            alert('Could not download or decrypt message: ' + (err.message || err));
            setDecryptingIndex(null);
        }
    };

    // DECRYPT video (from stegoFramePath)
    const handleRetrieveTextFromVideo = async (stegoFramePath, mediaUrl, frameTimestamp, index) => {
        setDecryptingIndex(index);
        try {
            if (!stegoFramePath) {
                alert('No stego frame available for this video.');
                setDecryptingIndex(null);
                return;
            }

            const myPrivateKey = localStorage.getItem(`privateKey_${userId}`);
            if (!myPrivateKey) {
                alert('Private key not found.');
                setDecryptingIndex(null);
                return;
            }

            const frameResponse = await axios.get(`http://localhost:3001${stegoFramePath}`, { responseType: 'blob' });

            const reader = new FileReader();
            reader.onload = async () => {
                try {
                    const frameDataUrl = reader.result;
                    const encryptedDataString = await extractDataFromImage(frameDataUrl);

                    if (!encryptedDataString) {
                        alert('No hidden data in frame.');
                        setDecryptingIndex(null);
                        return;
                    }

                    let encryptedPayload;
                    try {
                        encryptedPayload = JSON.parse(encryptedDataString);
                    } catch (e) {
                        console.error('JSON parse error:', e);
                        alert('Corrupted hidden data in frame.');
                        setDecryptingIndex(null);
                        return;
                    }

                    const decryptedMessage = decryptMessage(encryptedPayload, myPrivateKey);
                    const phishingLabel = await classifyMessage(decryptedMessage);

                    setMessages(prev => {
                        const updated = [...prev];
                        updated[index] = {
                            ...updated[index],
                            content: decryptedMessage,
                            isDecrypted: true,
                            phishingResult: phishingLabel
                        };
                        return updated;
                    });

                } catch (err) {
                    console.error('Video decryption error:', err);
                    alert('Failed to decrypt video message: ' + (err.message || err));
                } finally {
                    setDecryptingIndex(null);
                }
            };
            reader.onerror = (e) => {
                console.error('Frame reader error:', e);
                setDecryptingIndex(null);
            };
            reader.readAsDataURL(frameResponse.data);
        } catch (err) {
            console.error('Outer video error:', err);
            setDecryptingIndex(null);
            alert('Could not retrieve stego frame: ' + (err.message || err));
        }
    };

    const handleLogout = () => window.location.reload();

    return (
        <div className="chat-container">
            <div className="sidebar">
                <h3>Users</h3>
                <ul>
                    {users.map((user) => (
                        <li key={user} onClick={() => setRecipient(user)} className={recipient === user ? 'active' : ''}>
                            {user}
                        </li>
                    ))}
                </ul>
            </div>

            <div className="main-chat">
                <div className="chat-header">
                    <h2>Chatting with: {recipient || 'Select a user'}</h2>
                    <p>Logged in as: <strong>{userId}</strong></p>
                    <button onClick={handleLogout}>Logout</button>
                </div>

                <div className="message-window">
                    {messages.map((msg, index) => (
                        <div key={index} className={`message ${msg.sender}`}>
                            <div className="message-text">
                                <strong>Message:</strong> {msg.content}
                            </div>

                            {msg.isDecrypted && msg.phishingResult && (
                                <div style={{
                                    marginTop: "6px",
                                    padding: "8px",
                                    borderRadius: "6px",
                                    background: msg.phishingResult === "fake" ? "#ffcccc" : "#ccffcc",
                                    color: msg.phishingResult === "fake" ? "#b30000" : "#006600"
                                }}>
                                    {msg.phishingResult === "fake" ? "üö® PHISHING MESSAGE DETECTED" : "‚úî SAFE MESSAGE"}
                                </div>
                            )}

                            {msg.mediaType === 'image' && (
                                <div className="stego-image-container">
                                    <img src={`http://localhost:3001${msg.mediaUrl}`} alt="Hidden" />
                                    {!msg.isDecrypted && (
                                        <button onClick={() => handleRetrieveText(msg.mediaUrl, index)} disabled={decryptingIndex === index}>
                                            {decryptingIndex === index ? '‚è≥ Decrypting...' : 'Retrieve Text from Image'}
                                        </button>
                                    )}
                                </div>
                            )}

                            {msg.mediaType === 'video' && (
                                <div className="stego-video-container">
                                    <video width="320" height="240" controls>
                                        <source src={`http://localhost:3001${msg.mediaUrl}`} type="video/mp4" />
                                    </video>
                                    {!msg.isDecrypted && (
                                        <button onClick={() => handleRetrieveTextFromVideo(msg.stegoFramePath, msg.mediaUrl, msg.frameTimestamp, index)} disabled={decryptingIndex === index}>
                                            {decryptingIndex === index ? '‚è≥ Decrypting...' : 'Retrieve Text from Video'}
                                        </button>
                                    )}
                                </div>
                            )}
                        </div>
                    ))}
                </div>

                {/* INPUT: Enabled only when logged-in user is allowed to send to the selected recipient */}
                <div className="message-input">
                    <div className="carrier-type-selector">
                        <label>
                            <input type="radio" value="image" checked={selectedCarrierType === 'image'} onChange={(e) => { setSelectedCarrierType(e.target.value); setSelectedFile(null); }} />
                            Image
                        </label>
                        <label>
                            <input type="radio" value="video" checked={selectedCarrierType === 'video'} onChange={(e) => { setSelectedCarrierType(e.target.value); setSelectedFile(null); }} />
                            Video
                        </label>
                    </div>

                    <input type="file" accept={selectedCarrierType === 'image' ? "image/png, image/jpeg" : "video/mp4, video/webm, video/quicktime"} onChange={(e) => setSelectedFile(e.target.files[0])} />

                    <input type="text" placeholder="Type a message..." value={currentMessage} onChange={(e) => setCurrentMessage(e.target.value)} disabled={!recipient || !canSendToSelected()} />

                    <button onClick={sendMessage} disabled={!recipient || isSending || !canSendToSelected()}>
                        {isSending ? 'Sending...' : 'Send'}
                    </button>

                    {!canSendToSelected() && recipient && (
                        <div style={{ marginTop: 6, color: '#b30000' }}>You are not allowed to send to this recipient.</div>
                    )}
                </div>
            </div>
        </div>
    );
};

export default Chat;
