// src/components/Login.js

import React, { useState } from 'react';
import axios from 'axios';

// 1. Accept the new prop
const Login = ({ onLoginSuccess }) => { 
    const [userId, setUserId] = useState('');
    const [password, setPassword] = useState('');
    const [message, setMessage] = useState('');

    const handleLogin = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post('http://localhost:3001/api/login', {
                userId,
                password,
            });

            // Store the private key in localStorage (it's needed for decryption)
            // Note: In a real app, you'd need to retrieve the private key from the backend
            // For now, we assume it was saved during registration
            
            // 2. On success, call the function from App.js
            onLoginSuccess(response.data.token, response.data.userId);

        } catch (error) {
            if (error.response && error.response.data) {
                setMessage(error.response.data.message);
            } else {
                setMessage('Login failed. Server might be down.');
            }
        }
    };

    // ... keep the rest of the return() function the same
    return (
        <div className="auth-container">
            <h2>Login</h2>
            <form onSubmit={handleLogin}>
                {/* Form inputs are unchanged */}
                <input
                    type="text"
                    placeholder="Enter User ID"
                    value={userId}
                    onChange={(e) => setUserId(e.target.value)}
                    required
                />
                <input
                    type="password"
                    placeholder="Enter Password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    required
                />
                <button type="submit">Login</button>
            </form>
            {message && <p className="message">{message}</p>}
        </div>
    );
};

export default Login;
