// src/components/Register.js

import React, { useState } from 'react';
import axios from 'axios';

const Register = () => {
    const [userId, setUserId] = useState('');
    const [password, setPassword] = useState('');
    const [message, setMessage] = useState('');

    const handleRegister = async (e) => {
        e.preventDefault(); // Prevent form from reloading the page
        
        // --- REPLACE THE BLOCK BELOW ---
        try {
            const response = await axios.post('http://localhost:3001/api/register', { userId, password });
            
            // NEW: This line saves the private key to the browser's local storage
            localStorage.setItem(`privateKey_${userId}`, response.data.privateKey);
            
            setMessage(response.data.message); // Display success message
        } catch (error) {
            // Display error message from the backend
            if (error.response && error.response.data) {
                setMessage(error.response.data.message);
            } else {
                setMessage('Registration failed. Server might be down.');
            }
        }
        // --- END OF REPLACEMENT ---
    };

    return (
        <div className="auth-container">
            <h2>Register</h2>
            <form onSubmit={handleRegister}>
                <input
                    type="text"
                    placeholder="Enter a User ID"
                    value={userId}
                    onChange={(e) => setUserId(e.target.value)}
                    required
                />
                <input
                    type="password"
                    placeholder="Enter a Password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    required
                />
                <button type="submit">Register</button>
            </form>
            {message && <p className="message">{message}</p>}
        </div>
    );
};

export default Register;
