// src/components/Chat.js

import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import io from 'socket.io-client';
import {
    encryptMessage,
    decryptMessage,
    hideDataInImage,
    extractDataFromImage,
    hideDataInVideoFrame
} from '../utils/crypto';

const Chat = ({ userId }) => {

    const [users, setUsers] = useState([]);
    const [recipient, setRecipient] = useState('');
    const [currentMessage, setCurrentMessage] = useState('');
    const [messages, setMessages] = useState([]);
    const [selectedFile, setSelectedFile] = useState(null);
    const [selectedCarrierType, setSelectedCarrierType] = useState('image');
    const [isSending, setIsSending] = useState(false);
    const [isDecrypting, setIsDecrypting] = useState(false);   // â­ FIX ADDED
    const socket = useRef(null);

    // API â†’ phishing detection
    const classifyMessage = async (text) => {
        try {
            const res = await axios.post("http://localhost:3001/api/phishing-detect", {
                message: text
            });
            return res.data.label;
        } catch {
            return "unknown";
        }
    };

    // CONNECT SOCKET
    useEffect(() => {
        socket.current = io("http://localhost:3001");
        socket.current.emit("register_user", userId);

        socket.current.on("receive_message", (payload) => {
            const { mediaUrl, stegoFramePath, mediaType, frameTimestamp } = payload;

            if (!mediaUrl) return;

            setMessages(prev => [
                ...prev,
                {
                    sender: "other",
                    content: "[Encrypted Message - Click to Decrypt]",
                    mediaUrl,
                    stegoFramePath,
                    mediaType,
                    frameTimestamp,
                    isDecrypted: false,
                    phishingResult: null
                }
            ]);
        });

        axios.get("http://localhost:3001/api/users").then(res => {
            setUsers(res.data.filter(u => u !== userId));
        });

        return () => socket.current.disconnect();
    }, [userId]);

    // Convert DataURL â†’ Blob
    const dataURLtoBlob = (dataurl) => {
        const arr = dataurl.split(",");
        const mime = arr[0].match(/:(.*?);/)[1];
        const bstr = atob(arr[1]);
        let n = bstr.length;
        const u8arr = new Uint8Array(n);
        while (n--) u8arr[n] = bstr.charCodeAt(n);
        return new Blob([u8arr], { type: mime });
    };

    // SEND MESSAGE
    const sendMessage = async () => {
        if (!currentMessage || !recipient || !selectedFile) {
            alert("Type a message and select a carrier first.");
            return;
        }

        setIsSending(true);

        try {
            const res = await axios.get(`http://localhost:3001/api/key/${recipient}`);
            const encryptedPayload = encryptMessage(currentMessage, res.data.publicKey);
            const encryptedDataString = JSON.stringify(encryptedPayload);

            const reader = new FileReader();

            reader.onload = async (event) => {
                try {
                    let uploadData;
                    let mediaType = selectedCarrierType;

                    if (mediaType === "image") {
                        const stego = await hideDataInImage(event.target.result, encryptedDataString);
                        const blob = dataURLtoBlob(stego);

                        const formData = new FormData();
                        formData.append("carrier", blob, "stego.png");

                        uploadData = (await axios.post("http://localhost:3001/api/upload", formData)).data;

                    } else {
                        const result = await hideDataInVideoFrame(selectedFile, encryptedDataString);
                        const stegoFrameBlob = dataURLtoBlob(result.stegoFrame);

                        const formData = new FormData();
                        formData.append("carrier", selectedFile, selectedFile.name);
                        formData.append("stegoFrame", stegoFrameBlob, "frame.png");
                        formData.append("frameTimestamp", result.frameMetadata.frameTimestamp);

                        uploadData = (await axios.post("http://localhost:3001/api/upload", formData)).data;
                    }

                    socket.current.emit("send_message", {
                        recipientId: recipient,
                        message: {
                            mediaUrl: uploadData.filePath,
                            stegoFramePath: uploadData.stegoFramePath,
                            mediaType,
                            frameTimestamp: uploadData.frameTimestamp || null
                        }
                    });

                    // Sender sees their own message
                    setMessages(prev => [
                        ...prev,
                        {
                            sender: "me",
                            content: currentMessage,
                            mediaUrl: uploadData.filePath,
                            stegoFramePath: uploadData.stegoFramePath,
                            mediaType,
                            frameTimestamp: uploadData.frameTimestamp || null,
                            isDecrypted: true,
                            phishingResult: null
                        }
                    ]);

                    setCurrentMessage("");
                    setSelectedFile(null);

                } catch (err) {
                    alert("Error processing media.");
                }
            };

            reader.readAsDataURL(selectedFile);

        } finally {
            setIsSending(false);
        }
    };

    // DECRYPT IMAGE
    const handleRetrieveText = async (mediaUrl, index) => {

        if (isDecrypting) return;     // â­ FIX â†’ Prevent multiple decrypt runs
        setIsDecrypting(true);

        try {
            const privateKey = localStorage.getItem(`privateKey_${userId}`);
            if (!privateKey) {
                alert("Private key missing.");
                setIsDecrypting(false);
                return;
            }

            const mediaResponse = await axios.get(`http://localhost:3001${mediaUrl}`, {
                responseType: "blob"
            });

            const reader = new FileReader();
            reader.onload = async () => {

                try {
                    const encryptedString = await extractDataFromImage(reader.result);
                    const encryptedPayload = JSON.parse(encryptedString);

                    const decryptedMessage = decryptMessage(encryptedPayload, privateKey);
                    const phishingLabel = await classifyMessage(decryptedMessage);

                    const updated = [...messages];
                    updated[index] = {
                        ...updated[index],
                        content: decryptedMessage,
                        isDecrypted: true,
                        phishingResult: phishingLabel
                    };

                    setMessages(updated);

                } catch (err) {
                    alert("Failed to decrypt message.");
                }

                setIsDecrypting(false);
            };

            reader.readAsDataURL(mediaResponse.data);

        } catch {
            alert("Error reading image.");
            setIsDecrypting(false);
        }
    };

    // DECRYPT VIDEO FRAME
    const handleRetrieveTextFromVideo = async (stegoFramePath, index) => {

        if (isDecrypting) return;
        setIsDecrypting(true);

        try {
            const privateKey = localStorage.getItem(`privateKey_${userId}`);
            if (!privateKey) {
                alert("Missing private key.");
                setIsDecrypting(false);
                return;
            }

            const frameResponse = await axios.get(`http://localhost:3001${stegoFramePath}`, {
                responseType: "blob"
            });

            const reader = new FileReader();
            reader.onload = async () => {

                try {
                    const encryptedString = await extractDataFromImage(reader.result);
                    const encryptedPayload = JSON.parse(encryptedString);

                    const decryptedMessage = decryptMessage(encryptedPayload, privateKey);
                    const phishingLabel = await classifyMessage(decryptedMessage);

                    const updated = [...messages];
                    updated[index] = {
                        ...updated[index],
                        content: decryptedMessage,
                        isDecrypted: true,
                        phishingResult: phishingLabel
                    };

                    setMessages(updated);

                } catch {
                    alert("Video decryption failed.");
                }

                setIsDecrypting(false);
            };

            reader.readAsDataURL(frameResponse.data);

        } catch {
            alert("Error loading frame.");
            setIsDecrypting(false);
        }
    };

    // UI
    return (
        <div className="chat-container">
            <div className="sidebar">
                <h3>Users</h3>
                <ul>
                    {users.map((u) => (
                        <li key={u} onClick={() => setRecipient(u)}
                            className={recipient === u ? "active" : ""}>
                            {u}
                        </li>
                    ))}
                </ul>
            </div>

            <div className="main-chat">

                <div className="chat-header">
                    <h2>Chatting with: {recipient || "Select a user"}</h2>
                    <p>Logged in as: <strong>{userId}</strong></p>
                    <button onClick={() => window.location.reload()}>Logout</button>
                </div>

                <div className="message-window">
                    {messages.map((msg, index) => (
                        <div key={index} className={`message ${msg.sender}`}>

                            <div className="message-text">
                                <strong>Message: </strong>
                                {msg.content}
                            </div>

                            {msg.isDecrypted && msg.phishingResult && (
                                <div style={{
                                    marginTop: "6px",
                                    padding: "6px",
                                    borderRadius: "6px",
                                    background: msg.phishingResult === "fake"
                                        ? "#ffcccc" : "#ccffcc",
                                    color:
                                        msg.phishingResult === "fake" ? "#a30000" : "#007700"
                                }}>
                                    {msg.phishingResult === "fake"
                                        ? "ðŸš¨ PHISHING MESSAGE"
                                        : "âœ” SAFE MESSAGE"}
                                </div>
                            )}

                            {/* IMAGE */}
                            {msg.mediaType === "image" && (
                                <div className="stego-image-container">
                                    <img src={`http://localhost:3001${msg.mediaUrl}`} alt="hidden" />

                                    {!msg.isDecrypted && (
                                        <button
                                            disabled={isDecrypting}
                                            onClick={() => handleRetrieveText(msg.mediaUrl, index)}>
                                            {isDecrypting ? "Decrypting..." : "Retrieve Text"}
                                        </button>
                                    )}
                                </div>
                            )}

                            {/* VIDEO */}
                            {msg.mediaType === "video" && (
                                <div className="stego-video-container">
                                    <video width="320" height="240" controls>
                                        <source
                                            src={`http://localhost:3001${msg.mediaUrl}`}
                                            type="video/mp4"
                                        />
                                    </video>

                                    {!msg.isDecrypted && (
                                        <button
                                            disabled={isDecrypting}
                                            onClick={() => handleRetrieveTextFromVideo(msg.stegoFramePath, index)}>
                                            {isDecrypting ? "Decrypting..." : "Retrieve Text"}
                                        </button>
                                    )}
                                </div>
                            )}
                        </div>
                    ))}
                </div>

                <div className="message-input">

                    <div className="carrier-type-selector">
                        <label>
                            <input
                                type="radio"
                                value="image"
                                checked={selectedCarrierType === "image"}
                                onChange={(e) => {
                                    setSelectedCarrierType(e.target.value);
                                    setSelectedFile(null);
                                }}
                            /> Image
                        </label>

                        <label>
                            <input
                                type="radio"
                                value="video"
                                checked={selectedCarrierType === "video"}
                                onChange={(e) => {
                                    setSelectedCarrierType(e.target.value);
                                    setSelectedFile(null);
                                }}
                            /> Video
                        </label>
                    </div>

                    <input
                        type="file"
                        accept={selectedCarrierType === "image"
                            ? "image/*"
                            : "video/mp4, video/webm"}
                        onChange={(e) => setSelectedFile(e.target.files[0])}
                    />

                    <input
                        type="text"
                        placeholder="Type message..."
                        value={currentMessage}
                        onChange={(e) => setCurrentMessage(e.target.value)}
                        disabled={!recipient}
                    />

                    <button disabled={!recipient || isSending} onClick={sendMessage}>
                        {isSending ? "Sending..." : "Send"}
                    </button>
                </div>

            </div>
        </div>
    );
};

export default Chat;
