// src/App.js

import React, { useState } from 'react';
import Register from './components/Register';
import Login from './components/Login';
import Chat from './components/Chat'; // Import the new Chat component
import './App.css';

function App() {
    // State to hold the authentication token and user ID
    const [token, setToken] = useState(null);
    const [userId, setUserId] = useState('');

    // This function will be passed to the Login component
    const handleLoginSuccess = (newToken, loggedInUserId) => {
        setToken(newToken);
        setUserId(loggedInUserId);
    };

    return (
        <div className="App">
            <header className="App-header">
                <h1>Secure Chat Application</h1>
            </header>
            <main>
                {/* Conditional rendering: */}
                {/* If there's no token, show Login/Register */}
                {!token ? (
                    <div className="auth-wrapper">
                        <Register />
                        <Login onLoginSuccess={handleLoginSuccess} />
                    </div>
                ) : (
                    // If there is a token, show the Chat component
                    <Chat userId={userId} />
                )}
            </main>
        </div>
    );
}

export default App;
